function sendMail() {
    let name = document.getElementById("name").value;
    let email = document.getElementById("email").value;
    let subject = document.getElementById("subject").value;
    let message = document.getElementById("message").value;

    // Check if any fields are empty
    if (name === "" || email === "" || subject === "" || message === "") {
        document.getElementById("sentMessage").style.display = "none";
        document.getElementById("errorMessage").style.display = "block";
    } else {
        // Parameters to send via EmailJS
        let params = {
            from_name: name,
            email: email,
            subject: subject,
            message: message,
            replyTo: "danielabreu1738@gmail.com"
        };

        // Send email using EmailJS
        emailjs.send("service_13qv988", "template_e2ytbib", params)
            .then(
                function(response) {
                    console.log("SUCCESS!", response.status, response.text);
                },
                function(error) {
                    console.log("FAILED...", error);
                }
            );

        // Handle UI changes during loading and success states
        document.getElementById("errorMessage").style.display = "none";
        document.getElementById("loading").style.display = "block";

        // Simulate loading and show success message after a delay
        setTimeout(() => {
            document.getElementById("loading").style.display = "none";
            document.getElementById("sentMessage").style.display = "block";
        }, 3000);
    }
}
